dict.string:
#!/usr/bin/env bash
[[ -f "${TM_SUPPORT_PATH}/lib/bash_init.sh" ]] && . "${TM_SUPPORT_PATH}/lib/bash_init.sh"

get_pw () {
  /System/Library/Frameworks/Ruby.framework/Versions/1.8/usr/bin/ruby -rui -e"print TextMate::UI.request_secure_string(:title => 'Decrypt AES 128 Encrypted Text', :prompt => '$1', :button1 => '$2', :button2 => 'Cancel').to_s"
}

pw=$(get_pw 'What is the password?' Decrypt)
[[ -z "$pw" ]] && exit_discard

if ! openssl enc -d -aes128 -base64 -pass "pass:$pw"; then
   exit_show_tool_tip
fi
