dict.string:
#!/usr/bin/env bash STD_IN=$(</dev/stdin)
[[ -f "${TM_SUPPORT_PATH}/lib/bash_init.sh" ]] && . "${TM_SUPPORT_PATH}/lib/bash_init.sh"
get_pw () { ruby18 -rui -e"print TextMate::UI.request_secure_string(:title => 'Decrypt AES 256 Encrypted Text', :prompt => '$1', :button1 => '$2', :button2 => 'Cancel').to_s" }
pw=$(get_pw 'What is the password?' Decrypt) [[ -z "$pw" ]] && exit_discard
if ! echo $STD_IN|openssl enc -d -aes256 -md sha512 -a -A -pass "pass:$pw"; then exit_show_tool_tip fi
